		private void SolvePuzzle()
		{
			string t_Input = richTextBox1.Text;
			int t_Floor = 0;

			for (int i = 0; i < t_Input.Length; i++)
			{
				if      (t_Input[i] == '(') { t_Floor++; }
				else if (t_Input[i] == ')') { t_Floor--; }

				if (t_Floor == -1)
				{
					label2.Text = (i + 1).ToString();
					break;
				}
			}
		}
